towerdefenceApp.controller("GameContextCtrl",function($scope,$http,$routeParams,gameContextFactory){$scope.uiState="setTower";$scope.message="Debug Messages";gameContextFactory.updateNewGameContext(function(gameContext){$scope.gameContext=gameContext});$scope.clickOnField=function(columID,rowID){if($scope.uiState=="setTower"){$scope.message="setTower to "+columID+", "+rowID;var response=$http.post("/settower/columID/rowID",$scope.gameContext);response.success(function(dataFromServer,status,headers,
config){$scope.message=dataFromServer});response.error(function(data,status,headers,config){alert("Submitting GameContext failed!")})}else;};$scope.postFunction=function(){var response=$http.post("/post",$scope.gameContext);response.success(function(dataFromServer,status,headers,config){$scope.message=dataFromServer});response.error(function(data,status,headers,config){alert("Submitting GameContext failed!")})}});